ValidationRule.errorConditionFormula:
AND ( 
     OR(
        AND(
            Allow_Recurring_Order__c = TRUE,
            ISBLANK(TEXT(Recurring_Order_Frequency__c))
        ),
        AND(
            NOT(ISBLANK(TEXT(Recurring_Order_Frequency__c))),
            Allow_Recurring_Order__c = FALSE
        )
     ),
     $Setup.LVGO_Automation_Switch__c.Validations__c
)
