            Product2__r.Name = 'CV Solution',
            IF(UnitPrice__c < 55, TRUE, IF(UnitPrice__c > 85, TRUE, IF(UnitPrice__c > 60, IF(UnitPrice__c < 76, TRUE, FALSE),FALSE)))
            ),
            AND(
            Product2__r.Name = 'DP Solution',
            IF(UnitPrice__c < 73, TRUE, IF(UnitPrice__c > 85, TRUE, FALSE))
            )
            )
            )
